from langchain.chains.combine_documents.stuff import StuffDocumentsChain
from langchain.prompts import PromptTemplate
from langchain_openai import ChatOpenAI
from langchain_core.output_parsers import StrOutputParser
from langchain_core.pydantic_v1 import BaseModel, Field
from langchain_core.output_parsers import JsonOutputParser

def summary(content, max_token=12000):
    from langchain_core.documents.base import Document as LangchainDocument
    docs = LangchainDocument(page_content=content, metadata={})
    from langchain_text_splitters import CharacterTextSplitter
    from langchain.chains.summarize import load_summarize_chain
    # Define LLM chain
    llm = ChatOpenAI(temperature=0, model_name="gpt-3.5-turbo-16k") 
    chain = load_summarize_chain(llm, chain_type="refine")
    text_splitter = CharacterTextSplitter.from_tiktoken_encoder(
    chunk_size=max_token, chunk_overlap=0
    )
    split_docs = text_splitter.split_documents([docs])
    summary = chain.run(split_docs)
    return summary



# Function to dynamically determine the number of QA pairs based on text length
def determine_qa_count(text):
    length = len(text)
    # Simple rule-based conditions for determining number of QA pairs
    return ((length/500)+1)*5

# Define your desired data structure.
class qa(BaseModel):
    question: str = Field(description="question extracted from the document")
    answer: str = Field(description="answer extracted from the document")

class qaList(BaseModel):
    qa_list: list[qa] = Field(description="List of question and answer pairs extracted from the document")

# Updated function to process documents, extract QA pairs, and return JSON
def qa_extract(doc):
    parser = JsonOutputParser(pydantic_object=qaList)
    qa_count = determine_qa_count(doc)  # Dynamically determine the number of QA pairs
    # Template for the LLM prompt
    prompt_template = """请对以下文本进行提取问答对，请提取至少{qa_count}对:
    "{text}"
    {format_instructions}
    """
    prompt = PromptTemplate(
        template=prompt_template,
        input_variables=["text", "qa_count"],
        partial_variables={"format_instructions": parser.get_format_instructions()},
    )
    llm = prompt | ChatOpenAI(temperature=0, model_name="gpt-3.5-turbo-16k") |  parser
    return llm.invoke({'text':doc, 'qa_count':qa_count})


